import React, { useState } from 'react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const SubmissionForm = ({ onSubmissionSuccess }) => {
  const [formData, setFormData] = useState({
    teamName: '',
    githubLink: '',
    solution: ''
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.teamName.trim()) {
      newErrors.teamName = 'Team name is required';
    } else if (formData.teamName.trim().length > 100) {
      newErrors.teamName = 'Team name must be less than 100 characters';
    }

    if (!formData.githubLink.trim()) {
      newErrors.githubLink = 'GitHub link is required';
    } else {
      try {
        new URL(formData.githubLink);
        if (!formData.githubLink.includes('github.com')) {
          newErrors.githubLink = 'Please enter a valid GitHub repository URL';
        }
      } catch {
        newErrors.githubLink = 'Please enter a valid URL';
      }
    }

    if (!formData.solution.trim()) {
      newErrors.solution = 'Solution/Description is required';
    } else if (formData.solution.trim().length < 10) {
      newErrors.solution = 'Solution must be at least 10 characters long';
    } else if (formData.solution.trim().length > 2000) {
      newErrors.solution = 'Solution must be less than 2000 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);
    setSubmitMessage('');

    try {
      const response = await fetch(`${API_BASE_URL}/submit`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (data.success) {
        setSubmitMessage('Project submitted successfully!');
        setFormData({ teamName: '', githubLink: '', solution: '' });
        onSubmissionSuccess();
      } else {
        setSubmitMessage(data.message || 'Failed to submit project');
      }
    } catch (error) {
      console.error('Error:', error);
      setSubmitMessage('Error submitting project. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {/* Team Name */}
      <div className="group">
        <label htmlFor="teamName" className="block text-sm font-semibold text-gray-700 mb-3 group-focus-within:text-blue-600 transition-colors">
          Team Name *
        </label>
        <div className="relative">
          <input
            type="text"
            id="teamName"
            name="teamName"
            value={formData.teamName}
            onChange={handleChange}
            className={`w-full px-4 py-3 border-2 rounded-xl shadow-sm focus:outline-none transition-all duration-300 bg-white/50 backdrop-blur-sm ${
              errors.teamName 
                ? 'border-red-400 focus:border-red-500 focus:ring-red-200' 
                : 'border-gray-200 focus:border-blue-400 focus:ring-4 focus:ring-blue-100 hover:border-blue-300'
            }`}
            placeholder="Enter your team name"
          />
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none">
            <svg className="w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
            </svg>
          </div>
        </div>
        {errors.teamName && (
          <p className="mt-2 text-sm text-red-600 flex items-center">
            <svg className="w-4 h-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
            </svg>
            {errors.teamName}
          </p>
        )}
      </div>

      {/* GitHub Link */}
      <div>
        <label htmlFor="githubLink" className="block text-sm font-medium text-gray-700 mb-2">
          GitHub Repository Link *
        </label>
        <input
          type="url"
          id="githubLink"
          name="githubLink"
          value={formData.githubLink}
          onChange={handleChange}
          className={`w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
            errors.githubLink ? 'border-red-500' : 'border-gray-300'
          }`}
          placeholder="https://github.com/username/repository"
        />
        {errors.githubLink && (
          <p className="mt-1 text-sm text-red-600">{errors.githubLink}</p>
        )}
      </div>

      {/* Solution/Description */}
      <div>
        <label htmlFor="solution" className="block text-sm font-medium text-gray-700 mb-2">
          Solution / Description *
        </label>
        <textarea
          id="solution"
          name="solution"
          rows={4}
          value={formData.solution}
          onChange={handleChange}
          className={`w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
            errors.solution ? 'border-red-500' : 'border-gray-300'
          }`}
          placeholder="Describe your solution, approach, and key features..."
        />
        <div className="flex justify-between mt-1">
          {errors.solution ? (
            <p className="text-sm text-red-600">{errors.solution}</p>
          ) : (
            <p className="text-sm text-gray-500">
              {formData.solution.length}/2000 characters
            </p>
          )}
        </div>
      </div>

      {/* Submit Button */}
      <button
        type="submit"
        disabled={isSubmitting}
        className={`w-full py-2 px-4 rounded-md font-medium ${
          isSubmitting
            ? 'bg-gray-400 cursor-not-allowed'
            : 'bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2'
        } text-white transition duration-150 ease-in-out`}
      >
        {isSubmitting ? 'Submitting...' : 'Submit Project'}
      </button>

      {/* Success/Error Message */}
      {submitMessage && (
        <div className={`p-3 rounded-md ${
          submitMessage.includes('successfully') 
            ? 'bg-green-100 text-green-700 border border-green-200' 
            : 'bg-red-100 text-red-700 border border-red-200'
        }`}>
          {submitMessage}
        </div>
      )}
    </form>
  );
};

export default SubmissionForm;
