import React, { useState } from 'react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const SubmissionForm = ({ onSubmissionSuccess }) => {
  const [formData, setFormData] = useState({
    teamName: '',
    githubLink: '',
    solution: ''
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.teamName.trim()) {
      newErrors.teamName = 'Team name is required';
    } else if (formData.teamName.trim().length > 100) {
      newErrors.teamName = 'Team name must be less than 100 characters';
    }

    if (!formData.githubLink.trim()) {
      newErrors.githubLink = 'GitHub link is required';
    } else {
      try {
        new URL(formData.githubLink);
        if (!formData.githubLink.includes('github.com')) {
          newErrors.githubLink = 'Please enter a valid GitHub repository URL';
        }
      } catch {
        newErrors.githubLink = 'Please enter a valid URL';
      }
    }

    if (!formData.solution.trim()) {
      newErrors.solution = 'Solution/Description is required';
    } else if (formData.solution.trim().length < 10) {
      newErrors.solution = 'Solution must be at least 10 characters long';
    } else if (formData.solution.trim().length > 2000) {
      newErrors.solution = 'Solution must be less than 2000 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);
    setSubmitMessage('');

    try {
      const response = await fetch(`${API_BASE_URL}/submit`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (data.success) {
        setSubmitMessage('Project submitted successfully!');
        setFormData({ teamName: '', githubLink: '', solution: '' });
        onSubmissionSuccess();
      } else {
        setSubmitMessage(data.message || 'Failed to submit project');
      }
    } catch (error) {
      console.error('Error:', error);
      setSubmitMessage('Error submitting project. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {/* Team Name */}
      <div className="group">
        <label htmlFor="teamName" className="block text-sm font-semibold text-gray-700 mb-3 group-focus-within:text-blue-600 transition-colors">
          Team Name *
        </label>
        <div className="relative">
          <input
            type="text"
            id="teamName"
            name="teamName"
            value={formData.teamName}
            onChange={handleChange}
            className={`w-full px-4 py-3 border-2 rounded-xl shadow-sm focus:outline-none transition-all duration-300 bg-white/50 backdrop-blur-sm ${
              errors.teamName 
                ? 'border-red-400 focus:border-red-500 focus:ring-red-200' 
                : 'border-gray-200 focus:border-blue-400 focus:ring-4 focus:ring-blue-100 hover:border-blue-300'
            }`}
            placeholder="Enter your team name"
          />
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none">
            <svg className="w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
            </svg>
          </div>
        </div>
        {errors.teamName && (
          <p className="mt-2 text-sm text-red-600 flex items-center">
            <svg className="w-4 h-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
            </svg>
            {errors.teamName}
          </p>
        )}
      </div>

      {/* GitHub Link */}
      <div className="group">
        <label htmlFor="githubLink" className="block text-sm font-semibold text-gray-700 mb-3 group-focus-within:text-blue-600 transition-colors">
          GitHub Repository Link *
        </label>
        <div className="relative">
          <input
            type="url"
            id="githubLink"
            name="githubLink"
            value={formData.githubLink}
            onChange={handleChange}
            className={`w-full px-4 py-3 border-2 rounded-xl shadow-sm focus:outline-none transition-all duration-300 bg-white/50 backdrop-blur-sm ${
              errors.githubLink 
                ? 'border-red-400 focus:border-red-500 focus:ring-red-200' 
                : 'border-gray-200 focus:border-blue-400 focus:ring-4 focus:ring-blue-100 hover:border-blue-300'
            }`}
            placeholder="https://github.com/username/repository"
          />
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none">
            <svg className="w-5 h-5 text-gray-400" fill="currentColor" viewBox="0 0 24 24">
              <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
            </svg>
          </div>
        </div>
        {errors.githubLink && (
          <p className="mt-2 text-sm text-red-600 flex items-center">
            <svg className="w-4 h-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
            </svg>
            {errors.githubLink}
          </p>
        )}
      </div>

      {/* Solution/Description */}
      <div className="group">
        <label htmlFor="solution" className="block text-sm font-semibold text-gray-700 mb-3 group-focus-within:text-blue-600 transition-colors">
          Solution / Description *
        </label>
        <div className="relative">
          <textarea
            id="solution"
            name="solution"
            rows={4}
            value={formData.solution}
            onChange={handleChange}
            className={`w-full px-4 py-3 border-2 rounded-xl shadow-sm focus:outline-none transition-all duration-300 bg-white/50 backdrop-blur-sm resize-none ${
              errors.solution 
                ? 'border-red-400 focus:border-red-500 focus:ring-red-200' 
                : 'border-gray-200 focus:border-blue-400 focus:ring-4 focus:ring-blue-100 hover:border-blue-300'
            }`}
            placeholder="Describe your solution, approach, and key features..."
          />
          <div className="absolute top-3 right-3 pointer-events-none">
            <svg className="w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
            </svg>
          </div>
        </div>
        <div className="flex justify-between mt-2">
          {errors.solution ? (
            <p className="text-sm text-red-600 flex items-center">
              <svg className="w-4 h-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
              </svg>
              {errors.solution}
            </p>
          ) : (
            <p className="text-sm text-gray-500 font-medium">
              {formData.solution.length}/2000 characters
            </p>
          )}
        </div>
      </div>

      {/* Submit Button */}
      <button
        type="submit"
        disabled={isSubmitting}
        className={`group relative w-full py-4 px-6 rounded-xl font-bold text-lg transition-all duration-300 transform ${
          isSubmitting
            ? 'bg-gray-400 cursor-not-allowed scale-95'
            : 'bg-gradient-to-r from-blue-600 via-purple-600 to-blue-700 hover:from-blue-700 hover:via-purple-700 hover:to-blue-800 hover:scale-105 hover:shadow-2xl focus:outline-none focus:ring-4 focus:ring-blue-300 active:scale-95'
        } text-white shadow-lg`}
      >
        <span className="relative z-10 flex items-center justify-center">
          {isSubmitting ? (
            <>
              <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Submitting...
            </>
          ) : (
            <>
              <svg className="w-5 h-5 mr-2 group-hover:animate-bounce" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
              </svg>
              Submit Project
            </>
          )}
        </span>
        {!isSubmitting && (
          <div className="absolute inset-0 rounded-xl bg-gradient-to-r from-blue-600 via-purple-600 to-blue-700 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
        )}
      </button>

      {/* Success/Error Message */}
      {submitMessage && (
        <div className={`p-4 rounded-xl border-2 shadow-lg animate-slideIn ${
          submitMessage.includes('successfully') 
            ? 'bg-gradient-to-r from-green-50 to-emerald-50 text-green-800 border-green-200' 
            : 'bg-gradient-to-r from-red-50 to-pink-50 text-red-800 border-red-200'
        }`}>
          <div className="flex items-center">
            <div className={`w-8 h-8 rounded-full flex items-center justify-center mr-3 ${
              submitMessage.includes('successfully') ? 'bg-green-100' : 'bg-red-100'
            }`}>
              {submitMessage.includes('successfully') ? (
                <svg className="w-5 h-5 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                </svg>
              ) : (
                <svg className="w-5 h-5 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              )}
            </div>
            <span className="font-semibold">{submitMessage}</span>
          </div>
        </div>
      )}
    </form>
  );
};

export default SubmissionForm;
