import React, { useState, useEffect } from 'react';
import SubmissionForm from './components/SubmissionForm';
import SubmissionsTable from './components/SubmissionsTable';

// Apply dark mode to document
if (typeof document !== 'undefined') {
  document.documentElement.classList.add('dark');
}

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchSubmissions = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/submissions`);
      const data = await response.json();

      if (data.success) {
        setSubmissions(data.data);
      } else {
        setError('Failed to fetch submissions');
      }
    } catch (err) {
      setError('Error fetching submissions');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSubmissions();
  }, []);

  const handleSubmissionSuccess = () => {
    fetchSubmissions(); // Refresh the submissions list
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-dark-950 via-dark-900 to-dark-800 relative overflow-hidden">
      {/* Animated background decorations */}
      <div className="absolute top-0 left-0 w-full h-full overflow-hidden pointer-events-none">
        {/* Primary glow orbs */}
        <div className="absolute top-20 left-10 w-96 h-96 bg-gradient-to-r from-purple-600/30 to-blue-600/30 rounded-full filter blur-3xl animate-float-slow opacity-60"></div>
        <div className="absolute top-40 right-10 w-80 h-80 bg-gradient-to-r from-cyan-500/25 to-purple-500/25 rounded-full filter blur-3xl animate-pulse-slow animation-delay-2000 opacity-50"></div>
        <div className="absolute -bottom-8 left-20 w-72 h-72 bg-gradient-to-r from-pink-500/20 to-orange-500/20 rounded-full filter blur-3xl animate-float animation-delay-4000 opacity-40"></div>

        {/* Secondary accent orbs */}
        <div className="absolute top-1/3 left-1/3 w-32 h-32 bg-gradient-to-r from-emerald-400/20 to-teal-400/20 rounded-full filter blur-2xl animate-pulse opacity-30"></div>
        <div className="absolute bottom-1/4 right-1/4 w-48 h-48 bg-gradient-to-r from-indigo-500/15 to-purple-500/15 rounded-full filter blur-2xl animate-float-slow animation-delay-2000 opacity-35"></div>

        {/* Animated grid pattern */}
        <div className="absolute inset-0 bg-[linear-gradient(rgba(148,163,184,0.03)_1px,transparent_1px),linear-gradient(90deg,rgba(148,163,184,0.03)_1px,transparent_1px)] bg-[size:50px_50px] animate-pulse-slow"></div>
      </div>

      <div className="container mx-auto px-4 py-8 relative z-10">
        <header className="text-center mb-12">
          <div className="relative">
            <h1 className="text-7xl font-black gradient-text-neon mb-4 animate-neon-pulse tracking-tight">
              Niman2.0 2025
            </h1>
            {/* Floating decorative elements */}
            <div className="absolute -top-3 -right-3 w-6 h-6 bg-gradient-to-r from-yellow-400 to-orange-400 rounded-full animate-bounce shadow-lg shadow-yellow-400/50"></div>
            <div className="absolute -bottom-2 -left-3 w-4 h-4 bg-gradient-to-r from-pink-400 to-rose-400 rounded-full animate-ping shadow-lg shadow-pink-400/50"></div>
            <div className="absolute top-1/2 -right-8 w-3 h-3 bg-gradient-to-r from-cyan-400 to-blue-400 rounded-full animate-pulse shadow-lg shadow-cyan-400/50"></div>
          </div>
          <p className="text-xl text-dark-300 mb-8 font-medium leading-relaxed max-w-2xl mx-auto">
            Submit your innovative project and explore the creativity of fellow developers in this cutting-edge showcase
          </p>
          <div className="inline-flex items-center px-6 py-3 glass-dark rounded-full text-white text-sm font-medium shadow-2xl hover:shadow-purple-500/25 transition-all duration-300 hover:scale-105 animate-dark-glow">
            <span className="w-3 h-3 bg-gradient-to-r from-green-400 to-emerald-400 rounded-full mr-3 animate-pulse shadow-lg shadow-green-400/50"></span>
            Powered by <span className="font-bold bg-gradient-to-r from-cyan-400 to-blue-400 bg-clip-text text-transparent ml-2">Revamp</span>
            <div className="ml-2 w-2 h-2 bg-gradient-to-r from-purple-400 to-pink-400 rounded-full animate-ping"></div>
          </div>
        </header>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Submission Form */}
          <div className="glass-dark rounded-3xl shadow-2xl border border-dark-600/50 p-8 hover-lift hover:border-purple-500/50 transition-all duration-500 animate-dark-glow">
            <div className="flex items-center mb-8">
              <div className="w-14 h-14 bg-gradient-to-br from-blue-500 via-purple-600 to-cyan-500 rounded-2xl flex items-center justify-center mr-5 shadow-lg shadow-purple-500/30 animate-pulse">
                <svg className="w-7 h-7 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                </svg>
              </div>
              <h2 className="text-3xl font-bold gradient-text-neon">
                Submit Your Project
              </h2>
            </div>
            <SubmissionForm onSubmissionSuccess={handleSubmissionSuccess} />
          </div>

          {/* Submissions Table */}
          <div className="glass-dark rounded-3xl shadow-2xl border border-dark-600/50 p-8 hover-lift hover:border-emerald-500/50 transition-all duration-500">
            <div className="flex items-center mb-8">
              <div className="w-14 h-14 bg-gradient-to-br from-emerald-500 via-green-600 to-teal-500 rounded-2xl flex items-center justify-center mr-5 shadow-lg shadow-emerald-500/30 animate-pulse">
                <svg className="w-7 h-7 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <h2 className="text-3xl font-bold gradient-text-neon">
                All Submissions
              </h2>
            </div>
            {loading ? (
              <div className="flex justify-center items-center py-12">
                <div className="relative">
                  <div className="animate-spin rounded-full h-12 w-12 border-4 border-dark-600 border-t-purple-500 shadow-lg"></div>
                  <div className="absolute inset-0 animate-ping rounded-full h-12 w-12 border-2 border-purple-400 opacity-20"></div>
                </div>
              </div>
            ) : error ? (
              <div className="text-center py-12">
                <div className="inline-flex items-center px-6 py-4 bg-gradient-to-r from-red-500/20 to-pink-500/20 border border-red-500/30 rounded-2xl text-red-300 shadow-lg">
                  <svg className="w-6 h-6 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  {error}
                </div>
              </div>
            ) : (
              <SubmissionsTable submissions={submissions} />
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
